+
+      {/* Add Task */}
+      <div className="flex gap-2 mb-6">
+        <input
+          type="text"
+          placeholder="Enter a new task..."
+          className="flex-1 p-2 border rounded"
+          value={newTask}
+          onChange={(e) => setNewTask(e.target.value)}
+        />
+        <button
+          onClick={addTask}
+          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
+        >
+          Add
+        </button>
