import { useState, useEffect } from "react";
import axios from "axios";

//fetch tasks from backend
useEffect(() => {
  const fetchTasks = async () => {
    const { data } = await axios.get("http://localhost:3000/tasks");
    setTasks(data);
  };
  fetchTasks();
}, []);
