 @tailwind base;
 @tailwind components;
 @tailwind utilities;
+
+
+/* Futuristic Task Manager Styles */
+.futuristic-container {
+  min-height: 100vh;
+  background: linear-gradient(135deg, #0f2027, #203a43, #2c5364);
+  color: #fff;
+  font-family: 'Segoe UI', 'Roboto', sans-serif;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  padding: 20px;
+}
+
+.app-wrapper {
+  width: 100%;
+  max-width: 600px;
+  background: rgba(13, 25, 42, 0.6);
+  backdrop-filter: blur(10px);
+  border-radius: 20px;
+  padding: 30px;
+  box-shadow: 0 0 40px rgba(66, 220, 255, 0.2),
+              inset 0 0 20px rgba(255, 255, 255, 0.1);
+  border: 1px solid rgba(66, 220, 255, 0.3);
+}
+
+.app-title {
+  text-align: center;
+  font-size: 2.5rem;
+  font-weight: 700;
+  margin-bottom: 30px;
+  letter-spacing: 2px;
+  text-transform: uppercase;
+  background: linear-gradient(90deg, #42dcff, #ff00ff);
+  -webkit-background-clip: text;
+  -webkit-text-fill-color: transparent;
+  text-shadow: 0 0 10px rgba(66, 220, 255, 0.5);
+}
+
+.pulse {
+  animation: pulse 1.5s infinite;
+}
+
+@keyframes pulse {
+  0% { opacity: 1; }
+  50% { opacity: 0; }
+  100% { opacity: 1; }
+}
+
+.input-section {
+  display: flex;
+  justify-content: center;
+  margin-bottom: 40px;
+}
+
+.input-wrapper {
+  display: flex;
+  width: 100%;
+  max-width: 500px;
+  position: relative;
+}
+
+.futuristic-input {
+  flex: 1;
+  padding: 15px 20px;
+  border: none;
+  border-radius: 12px 0 0 12px;
+  background: rgba(255, 255, 255, 0.08);
+  color: #fff;
+  font-size: 16px;
+  outline: none;
+  border: 1px solid rgba(66, 220, 255, 0.3);
+  border-right: none;
+  box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.2),
+              0 0 15px rgba(66, 220, 255, 0.1);
+  transition: all 0.3s ease;
+}
+
+.futuristic-input:focus {
+  background: rgba(255, 255, 255, 0.12);
+  box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.2),
+              0 0 20px rgba(66, 220, 255, 0.3);
+}
+
+.futuristic-input::placeholder {
+  color: rgba(255, 255, 255, 0.5);
+}
+
+.futuristic-button {
+  padding: 15px 25px;
+  border: none;
+  border-radius: 0 12px 12px 0;
+  background: linear-gradient(90deg, #42dcff, #ff00ff);
+  color: white;
+  font-weight: 600;
+  font-size: 16px;
+  cursor: pointer;
+  position: relative;
+  overflow: hidden;
+  transition: all 0.3s ease;
+  box-shadow: 0 0 15px rgba(66, 220, 255, 0.4);
+}
+
+.futuristic-button:hover {
+  transform: translateY(-2px);
+  box-shadow: 0 0 20px rgba(66, 220, 255, 0.6);
+}
+
+.futuristic-button:active {
+  transform: translateY(0);
+}
+
+.button-overlay {
+  position: absolute;
+  top: 0;
+  left: -100%;
+  width: 100%;
+  height: 100%;
+  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
+  transition: all 0.6s ease;
+}
+
+.futuristic-button:hover .button-overlay {
+  left: 100%;
+}
+
+.tasks-container {
+  width: 100%;
+}
+
+.task-list {
+  list-style: none;
+  padding: 0;
+  margin: 0;
+}
+
+.task-item {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 15px 20px;
+  margin-bottom: 15px;
+  background: rgba(255, 255, 255, 0.05);
+  border-radius: 10px;
+  border: 1px solid rgba(66, 220, 255, 0.2);
+  position: relative;
+  overflow: hidden;
+  transition: all 0.3s ease;
+  animation: taskAppear 0.4s ease-out;
+}
+
+@keyframes taskAppear {
+  from {
+    opacity: 0;
+    transform: translateY(20px);
+  }
+  to {
+    opacity: 1;
+    transform: translateY(0);
+  }
+}
+
+.task-item:hover {
+  background: rgba(255, 255, 255, 0.08);
+  transform: translateY(-2px);
+  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
+}
+
+.task-text {
+  font-size: 16px;
+  letter-spacing: 0.5px;
+}
+
+.glow-bar {
+  width: 6px;
+  height: 100%;
+  background: linear-gradient(to bottom, #42dcff, #ff00ff);
+  position: absolute;
+  right: 0;
+  top: 0;
+  border-radius: 0 10px 10px 0;
+  opacity: 0.7;
+}
+
+.empty-state {
+  text-align: center;
+  padding: 40px 20px;
+  color: rgba(255, 255, 255, 0.6);
+}
+
+.hologram-icon {
+  font-size: 60px;
+  margin-bottom: 20px;
+  opacity: 0.7;
+  animation: rotate 8s linear infinite;
+}
+
+@keyframes rotate {
+  from { transform: rotate(0deg); }
+  to { transform: rotate(360deg); }
+}
+
+/* Responsive design */
+@media (max-width: 768px) {
+  .app-wrapper {
+    padding: 20px;
+  }
+
+  .app-title {
+    font-size: 2rem;
+  }
+
+  .input-wrapper {
+    flex-direction: column;
+  }
+
+  .futuristic-input {
+    border-radius: 12px;
+    border-right: 1px solid rgba(66, 220, 255, 0.3);
+    margin-bottom: 10px;
+  }
+
+  .futuristic-button {
+    border-radius: 12px;
+  }
+}
